const { createProxyMiddleware } = require('http-proxy-middleware');
 
module.exports = function (app) {
  app.use(
    createProxyMiddleware('/api3001', {
      target: 'https://localhost:3001', // API endpoint 1
      changeOrigin: true,
      pathRewrite: {
        "^/api3001": "",
      },
      headers: {
        Connection: "keep-alive"
      }
    })
  );
  app.use(
    createProxyMiddleware('/api5000', {
      target: 'https://localhost:5000', // API endpoint 2
      changeOrigin: true,
      pathRewrite: {
        "^/api5000": "",
      },
      headers: {
        Connection: "keep-alive"
      }
    })
  );
  app.use(
    createProxyMiddleware('/api81', {
      target: 'https://localhost:81', // API endpoint 3
      changeOrigin: true,
      pathRewrite: {
        "^/api81": "",
      },
      headers: {
        Connection: "keep-alive"
      }
    })
  );
  app.use(
    createProxyMiddleware('/api82', {
      target: 'https://localhost:82', // API endpoint 4
      changeOrigin: true,
      pathRewrite: {
        "^/api82": "",
      },
      headers: {
        Connection: "keep-alive"
      }
    })
  );
}
